import subprocess

# 1. 执行 adb root
try:
    result = subprocess.run(
        "adb root",
        capture_output=True,
        text=True,
        shell=True
    )
    out1 = (result.stdout or "").strip()
    err1 = (result.stderr or "").strip()
except Exception as e:
    print(f"❌ 执行失败: adb root 异常: {e}")
else:
    # 这里同时接受“重启成root”和“已经是root”两种情况
    if (
        "restarting adbd as root" in out1
        or "adbd is already running as root" in out1
    ):
        # 2. adb shell pm clear ...
        try:
            result2 = subprocess.run(
                "adb shell pm clear com.tct.entitlement",
                capture_output=True,
                text=True,
                shell=True
            )
            out2 = (result2.stdout or "").strip()
            err2 = (result2.stderr or "").strip()
        except Exception as e:
            print(f"❌ 执行失败: 清除包异常: {e}")
        else:
            if "Success" in out2:
                print("✅ 执行成功: 已成功清除 com.tct.entitlement")
            else:
                msg = out2 if out2 else err2
                print(f"❌ 执行失败: 清除包失败: {msg}")
    else:
        # adb root 既不是重启，也不是已是root，就当失败
        msg = out1 if out1 else err1
        print(f"❌ 执行失败: adb root 失败: {msg}")

import subprocess

# 1️⃣ 执行 adb root
try:
    result = subprocess.run("adb root", capture_output=True, text=True, shell=True)
    out1 = (result.stdout or "").strip()
    err1 = (result.stderr or "").strip()
except Exception as e:
    print(f"❌ 执行失败: adb root 异常: {e}")
else:
    if (
        "restarting adbd as root" in out1
        or "adbd is already running as root" in out1
    ):
        print("✅ adb root 成功")

        # 2️⃣ 删除目录
        try:
            result2 = subprocess.run("adb shell rm /data/deviceInfo/*",
                                     capture_output=True, text=True, shell=True)
            out2 = (result2.stdout or "").strip()
            err2 = (result2.stderr or "").strip()
        except Exception as e:
            print(f"❌ 执行失败: 删除目录异常: {e}")
        else:
            if not out2 and not err2:
                print("✅ 删除成功: /data/deviceInfo/*")
            elif "No such file" in err2:
                print("⚠️ 没有找到文件 (目录可能已空): /data/deviceInfo/*")
            elif "Permission denied" in err2:
                print("❌ 删除失败: 权限不足")
            else:
                print(f"❌ 删除失败: {err2 or out2}")
    else:
        msg = out1 if out1 else err1
        print(f"❌ adb root 失败: {msg}")

import subprocess

cmd = "adb shell am start -n com.debug.loggerui/.MainActivity"

try:
    result = subprocess.run(cmd, capture_output=True, text=True, shell=True)
    out = (result.stdout or "").strip()
    err = (result.stderr or "").strip()
except Exception as e:
    print(f"❌ 执行失败: 命令异常: {e}")
else:
    # 合并输出
    output = f"{out}\n{err}".strip()
    print(output)

    # 判断结果
    if "does not exist" in output or "Error type" in output:
        print("❌ 启动失败: Activity 不存在或出错，请安装 MTK Logger apk后再试")
    elif "Warning: Activity not started" in output or "Starting: Intent" in output:
        print("✅ 启动成功: Logger 已经调用成功")
    else:
        print("⚠️ 未知状态，请检查输出")

import subprocess

cmd = "adb uninstall com.debug.loggerui"

try:
    result = subprocess.run(cmd, capture_output=True, text=True, shell=True)
    out = (result.stdout or "").strip()
    err = (result.stderr or "").strip()
except Exception as e:
    print(f"❌ 执行失败: 命令异常: {e}")
else:
    # 合并输出
    output = f"{out}\n{err}".strip()
    print(output)

    # 判断结果
    if "Success" in output:
        print("✅ 卸载成功: 已成功卸载 MTK Logger")
    elif "Failure" in output or "DELETE_FAILED" in output or "not installed" in output:
        print("❌ 无法卸载 MTK Logger，或者设备没有安装 MTK Logger")
    else:
        print("⚠️ 未知状态，请检查输出结果")

import subprocess

cmd = "adb reboot edl"

try:
    result = subprocess.run(cmd, capture_output=True, text=True, shell=True)
    out = (result.stdout or "").strip()
    err = (result.stderr or "").strip()
except Exception as e:
    print(f"❌ 执行失败: 命令异常: {e}")
else:
    output = f"{out}\n{err}".strip()
    # 打印原始输出方便调试
    if output:
        print(output)

    # 判断成功与否
    if "no devices/emulators found" in output:
        print("❌ 请连接手机或者请打开 USB 调试")
    elif not output:
        print("✅ 命令执行成功，设备已进入 EDL 模式")
    else:
        print("⚠️ 未知状态，请检查输出")
